import { Injectable } from '@angular/core';
import {User, UsersResponse} from "./models/user";
import {ProductsResponse} from "./models/product-item";
import {HttpClient} from "@angular/common/http";
import {Observable} from "rxjs";

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  users: User[] = [{
    id: 1,
    firstName: "Terry",
    lastName: "Medhurst",
    gender: "male",
    email: "atuny0@sohu.com",
    phone: "+63 791 675 8914",
    password: "9uQFF1Lh",
    birthDate: "2000-12-25"
  }];

  constructor(private http:HttpClient) { }

  getUsers() {
    return this.users;
  }

  getUserById(id: number) {
    return this.http.get<ProductsResponse>('https://dummyjson.com/users/'+id);
  }

  addUser(user: User): Observable<User> {
    this.users?.push(user);
    return this.http.post<User>("https://dummyjson.com/users/add", user);
  }

  findUser(email: string, password: string): User | undefined {
    return this.users.find(user => user.email === email && user.password === password);
  }
}
