import { Component } from '@angular/core';
import {AuthService} from "../auth.service";
import {User} from "../models/user";

@Component({
  selector: 'app-userauth',
  templateUrl: './user-auth.component.html',
  styleUrls: ['./user-auth.component.css']
})
export class UserAuthComponent {

  authStatus : boolean = false;
  constructor(private authService:AuthService){}

  checkUser(email : string, password : string){
    if (this.authService.findUser(email, password)){
      this.set
    }
    else {
      alert("wrong credentials")
    }
  }
}
<section class="vh-100 gradient-custom">
  <div class="container py-5 h-100">
    <div class="row d-flex justify-content-center align-items-center h-100">
      <div class="col-12 col-md-8 col-lg-6 col-xl-5">
        <div class="card bg-dark text-white" style="border-radius: 1rem;">
          <div class="card-body p-5 text-center">

            <div class="mb-md-5 mt-md-4 pb-5">

              <h2 class="fw-bold mb-2 text-uppercase">Login</h2>
              <p class="text-white-50 mb-5">Please enter your login and password!</p>

              <div class="form-outline form-white mb-4">
                <input type="email" id="typeEmailX" class="form-control form-control-lg" #email />
                <label class="form-label" for="typeEmailX">Email</label>
              </div>

              <div class="form-outline form-white mb-4">
                <input type="password" id="typePasswordX" class="form-control form-control-lg" #password/>
                <label class="form-label" for="typePasswordX">Password</label>
              </div>

              <button class="btn btn-outline-light btn-lg px-5" type="submit" (click)="checkUser(email.value, password.value)" routerLink="/" routerLinkActive="active">Login</button>

            </div>

            <div>
              <p class="mb-0">Don't have an account? <a [href]="" class="text-white-50 fw-bold">Sign Up</a>
              </p>
            </div>

          </div>
        </div>
      </div>
    </div>
  </div>
</section>
