import {Component, OnInit} from '@angular/core';
import {NgForm} from "@angular/forms";
import {AuthService} from "../auth.service";
import {User} from "../models/user";
import {NgbDateStruct} from "@ng-bootstrap/ng-bootstrap";

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit{

  authUser: User | any;


  constructor(private authService: AuthService) {
  }

  submitEdit(ngForm: NgForm) {

    this.authUser.firstName = ngForm.value["firstName"];
    this.authUser.lastName = ngForm.value["lastName"];
    this.authUser.username = ngForm.value["username"];
    this.authUser.phone = ngForm.value["phone"];

    let selectedDate: NgbDateStruct = ngForm.value["birthDate"];
    this.authUser.birthDate = new Date(selectedDate.year, selectedDate.month - 1, selectedDate.day);



    let jsonString :any = JSON.stringify(this.authUser);

    this.authService.update(jsonString).subscribe((response:any)=>{
      console.log(response.body);
    })


  }

  ngOnInit(): void {
    let id = this.authService.getAuthUser();
    this.authService.getById(id).subscribe((response: any) => {
      response.json().then((data : any) => {
        console.log(data)
        this.authUser = data
      });});

  }
}
