import {Component, OnInit} from '@angular/core';
import {ProductService} from "../product.service";
import {CommandService} from "../command.service";
import {Command} from "../models/command";
import {AuthService} from "../auth.service";
import {ProductItem} from "../models/product-item";

@Component({
  selector: 'app-commands-list',
  templateUrl: './commands-list.component.html',
  styleUrls: ['./commands-list.component.css']
})
export class CommandsListComponent implements OnInit{

  commands: Command[] | undefined;

  constructor(private productService : ProductService, private commandService : CommandService, private authService : AuthService) {
  }

  ngOnInit(): void {
    this.commandService.getCommandsById(this.authService.getAuthUser()).subscribe(response => {
      this.commands = response;
    });
  }

  getProduct(id: number):ProductItem{
    let product: ProductItem ={
      brand: "",
      category: "",
      description: "",
      discountPercentage: 0,
      id: 0,
      images: [],
      price: 0,
      rating: 0,
      stock: 0,
      thumbnail: "",
      title: ""
    };
    this.productService.getProductById(id).subscribe(response => {
      product = response;
    })
    return product;
  }

}
<div class="row" style="display: flex; justify-content: center; align-items: center; height: 100vh">
  <div class="col-xxl-8 col-12">
    <!-- card -->
    <div class="card">
      <!-- card body-->
      <div class="card-body">
        <div class="mb-6">
          <h4 class="mb-0">Commandes</h4>
          <p></p>
        </div>
        <div class="mb-8" *ngFor="let command of commands">
          <!-- Commande -->
          <div class="border-bottom mb-3 pb-3 d-lg-flex align-items-center justify-content-between ">
            <div class="d-flex align-items-center justify-content-between">
              <h5 class="mb-0">Id {{command.date}}</h5> <!-- Command date + id? -->
            </div>
          </div>
          <!-- details Commande -->
          <div class="row justify-content-between align-items-center" *ngFor="let detail of command.details">
            <!-- col -->
            <div class="col-lg-8 col-12">
              <div class="d-md-flex">
                <div>
                  <!-- img -->
                  <img [src]="getProduct(detail.product).thumbnail">
                </div>
                <div class="ms-md-4 mt-2 mt-lg-0">
                  <!-- name + price -->
                  <h5 class="mb-1">
                    {{getProduct(detail.product).title}}
                  </h5>
                  <div class="mt-3">
                    <h4>{{getProduct(detail.product).price}}</h4>
                  </div>
                </div>
              </div>
            </div>

          </div>

        </div>
      </div>
    </div>
  </div>
</div>
