import { Injectable } from '@angular/core';
import {User, UsersResponse} from "./models/user";
import {ProductsResponse} from "./models/product-item";
import {HttpClient, HttpHeaders} from "@angular/common/http";
import {BehaviorSubject, from, Observable} from "rxjs";


@Injectable({
  providedIn: 'root'
})
export class AuthService {

  private authStatus = new BehaviorSubject<boolean>(this.isAuthenticated());

  httpOptions:any;

  constructor(private http:HttpClient) { }


  public isAuthenticated(): boolean {
    return localStorage.getItem('authStatus') === 'true';
  }

  getAuthStatus$() {
    return this.authStatus.asObservable();
  }

  public setAuthenticated(status: boolean): void {
    localStorage.setItem('authStatus', String(status));
    this.authStatus.next(status);
  }
  getUsers() {
    return this.http.get<UsersResponse>('http://localhost:4444/clients/');
  }

  setAuthenticatedUser(username: string){
    this.http.get<User>('http://localhost:4444/auth/authenticated/'+username).subscribe(response => {
      localStorage.setItem('authUser', JSON.stringify(response));
      this.authStatus.next(true);
    });
  }


  getAuthUser(): any {
    // @ts-ignore
    let user = JSON.parse(localStorage.getItem("authUser"));
    return user;
  }


  addUser(user: any): any {
    // this.httpOptions=new HttpHeaders({
    //     'Content-Type': 'application/json'}
    //   );
    // return this.http.post("http://localhost:4444/clients/add", JSON.stringify(user), this.httpOptions);

    return from(fetch("http://localhost:4444/auth/register", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: user
    }));
  }

  authenticate(username: string, password: string): any {
    let fd = new FormData();
    fd.append("username", username);
    fd.append("password", password);

    let plainObject:any = {};
    fd.forEach((value: any, key: any) => plainObject[key] = value);

    let jsonString :any = JSON.stringify(plainObject);

    return from(fetch("http://localhost:4444/auth/authenticate", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: jsonString
    }));
  }
}
