import {Component, OnChanges, OnInit, SimpleChanges} from '@angular/core';
import {CartService} from "../cart.service";
import {Router} from "@angular/router";

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent{

  authStatus : boolean = false;


  constructor( private cartService : CartService, private router : Router) {
  }

  getCount() {
    return this.cartService.getCount();
  }

  goToCart(event: { preventDefault: () => void; }) {
    event.preventDefault();
    this.router.navigate(['/cart']);
  }

  goToHome(event: { preventDefault: () => void; }) {
    event.preventDefault();
    this.router.navigate(['/products']);
  }

  signIn(){
    this.authStatus=true;
  }
}
<nav class="navbar navbar-expand-lg navbar-light bg-light">
  <div class="collapse navbar-collapse">
    <ul class="navbar">
      <li>
        <a class = "nav-link" href="#" (click)="goToHome($event)">
          <i class="bi-house" routerLinkActive="active" style="margin-left: 5px"> Catalogue</i>
        </a>
      </li>

    </ul>
    <ul class="navbar-nav ms-auto" style="margin-right: 30px">
      <li class="nav-item">
        <a class="nav-link" href="#" (click)="goToCart($event)">
          <i class="bi bi-cart"  routerLinkActive="active" ><span class="badge bg-danger" style="margin-left: 5px">{{getCount()}}</span></i>
        </a>
      </li>
    </ul>
    <ul class="navbar-nav" style="margin-right: 10px">
      <li class="nav-item">
        <a class="nav-link" routerLink="#">
          <a class="btn btn-primary" *ngIf="!authStatus ; else elseBlock" (click)="signIn()">Sign in</a>
          <ng-template #elseBlock>
            <nav class="navbar fixed-top navbar-expand-lg navbar-light bg-light">
              <a class="navbar-brand" href="#">Navbar</a>
              <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavDropdown" aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
                <span class="navbar-toggler-icon"></span>
              </button>
              <div class="collapse navbar-collapse" id="navbarNavDropdown">
                <ul class="navbar-nav w-100">
                  <li class="nav-item active">
                    <a class="nav-link" href="#">Home <span class="sr-only">(current)</span></a>
                  </li>
                  <li class="nav-item">
                    <a class="nav-link" href="#">Features</a>
                  </li>
                  <li class="nav-item">
                    <a class="nav-link" href="#">Pricing</a>
                  </li>
                  <li class="nav-item dropdown ml-auto">
                    <a class="nav-link dropdown-toggle" href="#" id="navbarDropdownMenuLink" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false"> Dropdown link </a>
                    <div class="dropdown-menu dropdown-menu-right" aria-labelledby="navbarDropdownMenuLink">
                      <a class="dropdown-item" href="#">Action</a>
                      <a class="dropdown-item" href="#">Another action</a>
                      <a class="dropdown-item" href="#">Something else here</a>
                    </div>
                  </li>
                </ul>
              </div>
            </nav>
          </ng-template>
        </a>
      </li>
    </ul>
  </div>
</nav>
