import {Component, OnChanges, OnInit, SimpleChanges} from '@angular/core';
import {CartService} from "../cart.service";
import {Router} from "@angular/router";

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent{

  authStatus : boolean;


  constructor( private cartService : CartService, private router : Router) {
  }

  getCount() {
    return this.cartService.getCount();
  }

  goToCart(event: { preventDefault: () => void; }) {
    event.preventDefault();
    this.router.navigate(['/cart']);
  }

  goToHome(event: { preventDefault: () => void; }) {
    event.preventDefault();
    this.router.navigate(['/products']);
  }

  signIn(){

  }
}
<nav class="navbar navbar-expand-lg navbar-light bg-light">
  <div class="collapse navbar-collapse">
    <ul class="navbar">
      <li>
        <a class = "nav-link" href="#" (click)="goToHome($event)">
          <i class="bi-house" routerLinkActive="active" style="margin-left: 5px"> Catalogue</i>
        </a>
      </li>

    </ul>
    <ul class="navbar-nav ms-auto" style="margin-right: 30px">
      <li class="nav-item">
        <a class="nav-link" href="#" (click)="goToCart($event)">
          <i class="bi bi-cart"  routerLinkActive="active" ><span class="badge bg-danger" style="margin-left: 5px">{{getCount()}}</span></i>
        </a>
      </li>
    </ul>
    <ul class="navbar-nav" style="margin-right: 10px">
      <li class="nav-item">
        <a class="nav-link" href="#">
          <a class="btn btn-primary" *ngIf="!authStatus ; else elseBlock"></a>
          <ng-template #elseBlock>

          </ng-template>
        </a>
      </li>
    </ul>
  </div>
</nav>
