import {Component, Input, OnInit} from '@angular/core';
import {ProductItem} from "../models/product-item";
import {ProductDetailsComponent} from "../product-details/product-details.component";
// @ts-ignore
import Array from '$GLOBAL$';
import {ProductService} from "../product.service";


@Component({
  selector: 'app-list-product',
  templateUrl: './list-product.component.html',
  styleUrls: ['./list-product.component.css']
})
export class ListProductComponent implements OnInit{

  products : Array<ProductItem>;

  constructor(private productService : ProductService) {
  }

  selectProduct(product : ProductItem){
    this.dataService.setProduct(product);
  }

  getState(product : ProductItem){
    if (product.disponibilite){
      return "En Stock";
    }
    return "En Rupture De Stock"
  }

  getColor(product : ProductItem) : string {
    if (product.disponibilite){
      return "green";
    }
    return "red";
  }

  ngOnInit(): void {
    this.products = this.productService.getProducts();
  }
}
<div class="container">
  <div class="row row-cols-1 row-cols-md-4">
    <div class="col mb-4" *ngFor="let product of products">
      <div class="card h-100 custom-card">
        <div class="card-body">
        </div>
        <img class="card-img mx-auto d-block " [src]="product.url" alt="Product image">
        <div class="card-body">
          <h5 class="card-title">{{product.nom}}</h5>
          <p class="card-text text">{{product.prix}} Dhs</p>
          <span style="font-weight: bolder" [ngStyle]="{color: getColor(product)}">{{getState(product)}}</span>
        </div>
        <div class="card-footer">
<!--          <a class="btn btn-info" *ngIf="product.disponibilite; else nonDisponible" style="margin: 5px">Commander</a>-->
          <a class="btn btn-primary" style="margin : 0 30px 0 0" routerLink="product_details/{{product.id}}" routerLinkActive="active">Details</a>
        </div>
      </div>
    </div>
  </div>
</div>
