import { Injectable } from '@angular/core';
import {ProductItem} from "./models/product-item";
import {CartItem} from "./models/cart-item";
import {from} from "rxjs";
import {Command} from "./models/command";
import {AuthService} from "./auth.service";

@Injectable({
  providedIn: 'root'
})
export class CartService {

  private storageKey : string = 'cartItems';

  items : CartItem[] = [];
  constructor(private authService: AuthService) {
    if (!sessionStorage.getItem(this.storageKey)) {
      sessionStorage.setItem(this.storageKey, JSON.stringify([]));
    }
  }
  addToCart(product: ProductItem | null | undefined, qte:number) {
    if (product) {
      let cartItem = this.items.find(item => item.product.id === product.id);
      if (cartItem) {
        cartItem.qte += qte;
      } else {
        this.items.push(new CartItem(product, qte));
      }
    }
    sessionStorage.setItem('cartItems', JSON.stringify(this.items));
  }

  getItems() {
    let items = sessionStorage.getItem('cartItems');
    return items ? JSON.parse(items) : [];
  }

  clearCart() {
    this.items = [];
    sessionStorage.removeItem('cartItems');
  }

  getCount() {
    let items = this.getItems();
    return items.length;
  }

  validateCart(){

    // let fd: FormData = new FormData();
    //
    // fd.append("detailsCommandes", JSON.stringify(commande.details));
    // fd.append("client", JSON.stringify(commande.user));
    // fd.append("dateCommande", commande.date);
    //
    // console.log(fd);

    return from(fetch("http://localhost:4444/commande/add", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization' : 'Bearer '+this.authService.getToken()
      },
      body:"fd"
    }));
  }
}
