import { Injectable } from '@angular/core';
import {ProductItem} from "./models/product-item";
import {CartItem} from "./models/cart-item";

@Injectable({
  providedIn: 'root'
})
export class CartService {

  items : CartItem[] = [];
  constructor() { }

  addToCart(product: ProductItem | null | undefined) {
    if (product instanceof ProductItem) {
      let cartItem = this.items.find(item => item.product.id === product.id);
      if (cartItem) {
        cartItem.qte += 1;
      } else {
        this.items.push(new CartItem(product, 1));
      }
    }
  }

  getItems() {
    return this.items;
  }

  clearCart() {
    this.items = [];
    return this.items;
  }

}
