import { Injectable } from '@angular/core';
import {User, UsersResponse} from "./models/user";
import {ProductsResponse} from "./models/product-item";
import {HttpClient, HttpHeaders} from "@angular/common/http";
import {from, Observable} from "rxjs";

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  authStatus: boolean | undefined;


  httpOptions:any;

  constructor(private http:HttpClient) { }


  public isAuthenticated(): boolean {
    return <boolean>this.authStatus;
  }

  public setAuthenticated(status: boolean): void {
    this.authStatus = status;
  }
  getUsers():User[] {
    return fetch('http://localhost:4444/clients/')
      .then(response => response.json())
      .then(data => data);
  }

  getUserById(id: number) {
    return this.http.get<UsersResponse>('http://localhost:4444/clients/'+id);
  }

  addUser(user: any): any {
    // this.httpOptions=new HttpHeaders({
    //     'Content-Type': 'application/json'}
    //   );
    // return this.http.post("http://localhost:4444/clients/add", JSON.stringify(user), this.httpOptions);

    return from(fetch("http://localhost:4444/clients/add", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: user
    }));
  }

  findUser(email: string, password: string): User | undefined {
    return this.getUsers().find(user => user.email === email && user.password === password);
  }
}
