import { Injectable } from '@angular/core';
import {User, UsersResponse} from "./models/user";
import {ProductsResponse} from "./models/product-item";
import {HttpClient, HttpHeaders} from "@angular/common/http";
import {Observable} from "rxjs";

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  authStatus: boolean | undefined;

  users: User[] = [{
    id: 1,
    firstName: "Terry",
    lastName: "Medhurst",
    gender: "male",
    email: "atuny0@sohu.com",
    phone: "+63 791 675 8914",
    password: "9uQFF1Lh",
    birthDate: "2000-12-25"
  }];

  httpOptions:any;

  constructor(private http:HttpClient) { }


  public isAuthenticated(): boolean {
    return <boolean>this.authStatus;
  }

  public setAuthenticated(status: boolean): void {
    this.authStatus = status;
  }
  getUsers() {
    return this.http.get<UsersResponse>('http://localhost:4444/clients/');
  }

  getUserById(id: number) {
    return this.http.get<UsersResponse>('http://localhost:4444/clients/'+id);
  }

  addUser(user: FormData): any {
    this.httpOptions=new HttpHeaders(
        'Content-Type': 'application/json'
      );
    return this.http.post("http://localhost:4444/clients/add", user, this.httpOptions);
  }

  findUser(email: string, password: string): User | undefined {
    return this.users.find(user => user.email === email && user.password === password);
  }
}
