import { Injectable } from '@angular/core';
import {User, UsersResponse} from "./models/user";
import {ProductsResponse} from "./models/product-item";
import {HttpClient} from "@angular/common/http";
import {Observable} from "rxjs";

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  users: User[] | undefined;

  constructor(private http:HttpClient) { }

  getUsers() {
    return this.http.get<UsersResponse>('https://dummyjson.com/users').subscribe(response => {
      this.users = response.users.map(item =>({
        id: item.id;
      }));
    });
  }

  getUserById(id: number) {
    return this.http.get<ProductsResponse>('https://dummyjson.com/users/'+id);
  }

  addUser(user: User): Observable<User> {
    this.users?.push(user);
    return this.http.post<User>("https://dummyjson.com/users/add", user);
  }

  findUser(username: string, password: string): User | undefined {
    // @ts-ignore
    return this.users.find(user => user.username === username && user.password === password);
  }
}
