import {Component, OnInit} from '@angular/core';
import {NgForm} from "@angular/forms";
import {AuthService} from "../auth.service";
import {User} from "../models/user";
import {NgbDateStruct} from "@ng-bootstrap/ng-bootstrap";

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit{

  authUser: User | undefined;


  constructor(private authService: AuthService) {
  }

  submitEdit(ngForm: NgForm) {

    alert("submit");

    let fd = new FormData();
    fd.append("id", this.authService.getAuthUser());
    fd.append("firstName", ngForm.value["firstName"]);
    fd.append("lastName", ngForm.value["lastName"]);
    fd.append("username", ngForm.value["username"]);
    fd.append("phone", ngForm.value["phone"]);

    console.log(ngForm.value["firstName"]);

    // let selectedDate: NgbDateStruct = ngForm.value["birthDate"];
    // let jsDate:Date = new Date(selectedDate.year, selectedDate.month - 1, selectedDate.day);

    fd.append("birthDate", ngForm.value["birthDate"] );

    let plainObject:any = {};
    fd.forEach((value: any, key: any) => plainObject[key] = value);

    let jsonString :any = JSON.stringify(plainObject);

    this.authService.update(jsonString).subscribe((response:any)=>{
      console.log(response.body);
    })


  }

  ngOnInit(): void {
    let id = this.authService.getAuthUser();
    this.authService.getById(id).subscribe((response: any) => {
      response.json().then((data : any) => {
        console.log(data)
        this.authUser = data
      });});

  }
}
<section class="vh-100 gradient-custom">
  <div class="container py-5 h-100">
    <div class="row justify-content-center align-items-center h-100">
      <div class="col-12 col-lg-9 col-xl-7">
        <div class="card shadow-2-strong card-registration" style="border-radius: 15px;">
          <div class="card-body p-4 p-md-5">
            <h3 class="mb-4 pb-2 pb-md-0 mb-md-5">Update Info</h3>
            <form (ngSubmit)="submitEdit(userForm)" #userForm="ngForm">

              <div class="row">
                <div class="col-md-6 mb-4">

                  <div class="form-outline">
                    <input type="text" id="firstName" class="form-control form-control-lg" [value]="authUser?.firstName" name="firstName" ngModel />
                    <label class="form-label" for="firstName">First Name</label>
                  </div>

                </div>
                <div class="col-md-6 mb-4">

                  <div class="form-outline">
                    <input type="text" id="lastName" class="form-control form-control-lg" [value]="authUser?.lastName" name="lastName" ngModel/>
                    <label class="form-label" for="lastName">Last Name</label>
                  </div>

                </div>
              </div>

              <div class="row">
                <div class="col-md-6 mb-4 d-flex align-items-center">

                  <div class="form-group">
                    <label for="birthDate">Birthday</label>
                    <input class="form-control" id="birthDate" [value]="authUser?.birthDate" placeholder="yyyy-mm-dd"
                           name="birthDate" ngModel ngbDatepicker #d="ngbDatepicker" (click)="d.toggle()">
                  </div>

                </div>
              </div>

              <div class="row">
                <div class="col-md-6 mb-4 pb-2">

                  <div class="form-outline">
                    <input type="text" id="username" class="form-control form-control-lg" [value]="authUser?.username" name="username"  ngModel/>
                    <label class="form-label" for="username">Username</label>
                  </div>

                </div>
                <div class="col-md-6 mb-4 pb-2">

                  <div class="form-outline">
                    <input type="tel" id="phoneNumber" class="form-control form-control-lg" [value]="authUser?.phone" name="phone" ngModel/>
                    <label class="form-label" for="phoneNumber">Phone Number</label>
                  </div>

                </div>
              </div>


              <div class="mt-4 pt-2">
                <button class="btn btn-primary btn-lg" type="submit">Submit</button>
              </div>

            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
