import {Component, Input, OnInit} from '@angular/core';
import {ProductItem} from "../models/product-item";
import {ProductService} from "../product.service";
import {ActivatedRoute} from "@angular/router";
import {CartService} from "../cart.service";

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css']
})
export class ProductDetailsComponent implements OnInit{

  id : number | undefined ;
  product : ProductItem | null | undefined;


  constructor(private productService : ProductService, private cartService : CartService, private route : ActivatedRoute) {
  }

  ngOnInit(): void {
    this.route.params.subscribe(event => {
      this.id = event['id'];
    })
    if (this.id != null) {
      this.product = this.productService.getProductById(this.id);
    }
  }

  addToCart(product: ProductItem | null | undefined) {
    this.cartService.addToCart(product);
    window.alert(this.cartService.getItems());
  }
}
<div class="d-flex align-items-center justify-content-center" style="height: 100vh;">

  <div class="card w-50" >
    <div class="row g-0">
      <div class="col-md-4 d-flex align-items-center justify-content-center">
        <img src="{{product?.url}}" class="img-fluid" alt="Image">
      </div>
      <div class="col-md-8">
        <div class="card-body">
          <h5 class="card-title">{{product?.nom}}</h5>
          <p class="card-text">{{product?.description}}</p>
          <h6 class="card-text">{{product?.prix}} Dhs</h6>
        </div>
        <div class="card-footer">
          <a class="btn btn-primary" style="margin : 0 30px 0 0" (click)="addToCart(product)">Add to cart</a>
        </div>
      </div>
    </div>
  </div>

</div>
