import {Component, Input, OnInit} from '@angular/core';
import {ProductItem} from "../models/product-item";
import {ProductService} from "../product.service";
import {ActivatedRoute} from "@angular/router";
import {CartService} from "../cart.service";

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css']
})
export class ProductDetailsComponent implements OnInit{

  id : number | undefined ;
  product : ProductItem | null | undefined;


  constructor(private productService : ProductService, private cartService : CartService, private route : ActivatedRoute) {
  }

  ngOnInit(): void {
    this.route.params.subscribe(event => {
      this.id = event['id'];
    })
    if (this.id != null) {
      this.productService.getProductById(this.id)
        .subscribe(response => {
          this.product = response;
        });    }
  }

  addToCart(product: ProductItem | null | undefined, qte : number) {
    this.cartService.addToCart(product, qte);
    window.alert(this.cartService.getItems());
  }

  protected readonly parseInt = parseInt;
}


<div class="d-flex align-items-center justify-content-center" style="height: 70vh">

  <div class="card w-50" >
    <div class="row g-0">
      <div class="col-md-2 align-items-center justify-content-center" >
        <img src="{{product?.thumbnail}}" class="product-image img-fluid" alt="Image" >
        <div class="image-list">
          <img *ngFor="let image of product?.images"
               [src]="image"
               class="img-fluid image-item"
               alt="Product Image">
        </div>
      </div>
      <div class="col-md-8">
        <div class="card-body">
          <h5 class="card-title">{{product?.title}}</h5>
          <p class="card-text">{{product?.description}}</p>
          <h6 class="card-text">{{product?.price}} Dhs</h6>
        </div>
        <div class="card-footer">
          <input type="number" value="1" min="1" max="10" #quantity/>
          <a class="btn btn-primary" style="margin : 0 30px 0 0" (click)="addToCart(product, parseInt(quantity.value))">Add to cart</a>
        </div>
      </div>
    </div>
  </div>

</div>
